-- Я роняю запад
script_name("MO Helper")
script_author("Oscar Rain")
script_version_number(1)
script_version("1.0")

-- Цвет настроения синий
require "lib.moonloader"
require "lib.sampfuncs"
require "config.carmy"
local dlstatus = require('moonloader').download_status
local sampev = require "lib.samp.events"

function main()
    if not isSampLoaded() or not isSampfuncsLoaded() then error(''..script_name..' needs SA:MP and SAMPFUNCS!') end
    while not isSampAvailable() do wait(100) end
    -- Проверка обновлений
    update()
	while update ~= false do wait(100) end
    --
    sampAddChatMessage('[MO Helper] Загружено. Автор: Oscar Rain. Версия: '..thisScript().version..'', 0xD2B48C)
    sampRegisterChatCommand("доклад", doklad)
    sampRegisterChatCommand("присяга", function()
        lua_thread.create(oath)
    end)
    sampRegisterChatCommand("устав", function()
        lua_thread.create(ustav)
    end)
    while true do
		wait(1)
	end
end

function doklad()
    lua_thread.create(dokladd)
end

function ustav()
    sampShowDialog(6399, "Устав Министерства Обороны", "{FFA500}[1] {FFFFFF}Основное положение\n{FFA500}[2] {FFFFFF}Общение и субординация\n{FFA500}[3] {FFFFFF}Построение\n{FFA500}[4] {FFFFFF}Проникновение посторонних на территорию\n{FFA500}[5] {FFFFFF}Увольнительное время и выход за территорию военной базы\n{FFA500}[6] {FFFFFF}Склад оружия\n{FFA500}[7] {FFFFFF}Повышения\n{FFA500}[8] {FFFFFF}Отчеты и доклады\n{FFA500}[9] {FFFFFF}Главный Склад\n{FFA500}[10] {FFFFFF}Допуск на территорию Армии\n{FFA500}[11] {FFFFFF}Лаборатория\n{FFA500}[12] {FFFFFF}Внешний вид\n{FFA500}[13] {FFFFFF}United States Army Special Forces\n{FFA500}[14] {FFFFFF}Выговора/Демобилизация\n{FFA500}[15] {FFFFFF}Рация\n{FFA500}[16] {FFFFFF}Учения\n{FFA500}[17] {FFFFFF}Военная присяга\n{FFA500}[18] {FFFFFF}Наказания за нарушения устава [Часть №1]\n{FFA500}[19] {FFFFFF}Наказания за нарушения устава [Часть №2]\n{FFA500}[20] {FFFFFF}Наказания за нарушения устава [Часть №3]", "ОК", "Закрыть", DIALOG_STYLE_LIST)
    while sampIsDialogActive(6399) do wait(100) end
    local _, button, list, _ = sampHasDialogRespond(6399)
