function dokladd()
    sampShowDialog(6395, "Настройки", '{0000FF}1.{FFFFFF} Интервал: '..carmy.interval..' минут\n{0000FF}2.{FFFFFF} Повторять: '..carmy.repeatt..' раз(а)\n{0000FF}3.{FFFFFF} Автоскриншот: '..carmy.autoscreen..'\n{0000FF}4.{FFFFFF} Пост: '..carmy.post..'\n{0000FF}5.{FFFFFF} Старт\n{0000FF}6.{FFFFFF} Стоп', 'OK', 'Отмена', DIALOG_STYLE_LIST)
    while sampIsDialogActive(6395) do wait(100) end
    local result, button, list, input = sampHasDialogRespond(6395)
    local job = false
    if button == 1 and list == 0 then
        sampShowDialog(6396, "Настройки", "Введите интервал между докладами в рацию в минутах", "ОК", "Отмена", DIALOG_STYLE_INPUT)
        while sampIsDialogActive(6396) do wait(100) end
        local _, button, _, input = sampHasDialogRespond(6396)
        if button == 1 then
            carmy.interval = tonumber(input)
        end
        doklad()
    elseif button == 1 and list == 1 then
        sampShowDialog(6397, "Настройки", "2 раза\n3 раза\n4 раза\n5 раз\n6 раз\n7 раз\n8 раз\n9 раз\n10 раз\n11 раз\n12 раз\n13 раз\n14 раз\n15 раз", "ОК", "Отмена", DIALOG_STYLE_LIST)
        while sampIsDialogActive(6397) do wait(100) end
        local _, button, list, _ = sampHasDialogRespond(6397)
        if button == 1 then carmy.repeatt = list + 2 end
        doklad()
    elseif button == 1 and list == 2 then
        if carmy.autoscreen == "{008000}ON{FFFFFF}" then 
            carmy.autoscreen = "{FF0000}OFF{FFFFFF}"
        else 
            carmy.autoscreen = "{008000}ON{FFFFFF}"            
        end
        doklad()
    elseif button == 1 and list == 3 then
        sampShowDialog(6398, "Настройки", "Введите название, занимаемого вами поста", "ОК", "Отмена", DIALOG_STYLE_INPUT)
        while sampIsDialogActive(6398) do wait(100) end
        local _, button, _, input = sampHasDialogRespond(6398)
        if button == 1 then
            carmy.post = input
        end
        doklad()
    elseif button == 1 and list == 4 then
        local _, id = sampGetPlayerIdByCharHandle(PLAYER_PED)
        local name = sampGetPlayerNickname(id)
        name = name:gsub("%_", " ")
        job = true
        for i = 1, carmy.repeatt do
            if job == false then 
                sampAddChatMessage('[MO Helper] Автодоклад остановлен', 0xD2B48C)
                break;
            end
            sampSendChat('/r '..carmy.tagR..' Докладывает: '..name..' | Пост: '..carmy.post..' | Состояние: стабильно.')
            sampSendChat('/time')
            wait(400)
            if carmy.autoscreen == "{008000}ON{FFFFFF}" then 
                setVirtualKeyDown(VK_F8, true)
                wait(100)
                setVirtualKeyDown(VK_F8, false)
            end
            wait(500)
            sampAddChatMessage('[MO Helper] Выполнено '..i..'/'..carmy.repeatt..'', 0xD2B48C)
            wait(carmy.interval*60000)
        end
    elseif button == 1 and list == 5 then
        job = false
    end
end

function oath()
    local _, id = sampGetPlayerIdByCharHandle(PLAYER_PED)
    local name = sampGetPlayerNickname(id)
    name = name:gsub("%_", " ")
    sampSendChat('Я '..name..' торжественно клянусь сохранять верность Федеративным Штатам Trilliant.')
